import React from 'react';

// Simple button component for sidebar
function SidebarButton({ isActive, onClick, tabId, children }) {
  return (
    <button
      onClick={function() { onClick(tabId) }}
      className={`w-full flex items-center px-3 py-2 rounded-md text-sm transition-colors
        ${isActive ? 'bg-blue-600 text-white' : 'text-gray-300 hover:bg-gray-800'}`}
    >
      {children}
    </button>
  );
}

// TFSA Account Component
function TFSAContent() {
  return (
    <div className="p-6 max-w-4xl">
      <h1 className="text-2xl font-bold mb-6">TFSA Overview</h1>
      <div className="bg-white rounded-lg shadow-md p-6">
        <h3 className="text-lg font-semibold text-gray-700">Current Balance</h3>
        <p className="text-4xl font-bold text-gray-900 my-4">$12,450.00</p>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mt-4">
          <div>
            <span className="text-sm text-gray-500">YTD Return</span>
            <p className="text-lg font-semibold text-green-600">+8.2%</p>
          </div>
          <div>
            <span className="text-sm text-gray-500">Contribution Room</span>
            <p className="text-lg font-semibold text-gray-900">$6,000.00</p>
          </div>
        </div>
      </div>
    </div>
  );
}

// RESP Account Component
function RESPContent() {
  return (
    <div className="p-6 max-w-4xl">
      <h1 className="text-2xl font-bold mb-6">RESP Overview</h1>
      <div className="bg-white rounded-lg shadow-md p-6">
        <h3 className="text-lg font-semibold text-gray-700">Current Balance</h3>
        <p className="text-4xl font-bold text-gray-900 my-4">$25,780.00</p>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mt-4">
          <div>
            <span className="text-sm text-gray-500">Government Grants</span>
            <p className="text-lg font-semibold text-gray-900">$5,000.00</p>
          </div>
          <div>
            <span className="text-sm text-gray-500">Annual Return</span>
            <p className="text-lg font-semibold text-green-600">+6.8%</p>
          </div>
        </div>
      </div>
    </div>
  );
}

// Stock Details Component
function StockContent({ stockId }) {
  const stockData = {
    tesla: { name: 'Tesla', symbol: 'TSLA', price: 242.50, change: 2.5 },
    blackrock: { name: 'BlackRock', symbol: 'BLK', price: 785.20, change: -0.8 },
    pepsico: { name: 'PepsiCo', symbol: 'PEP', price: 172.30, change: 1.2 }
  };

  const stock = stockData[stockId];

  return (
    <div className="p-6 max-w-4xl">
      <h1 className="text-2xl font-bold mb-6">Stock Details</h1>
      <div className="bg-white rounded-lg shadow-md p-6">
        <div className="flex items-center gap-4 mb-6">
          <img 
            src="/api/placeholder/48/48" 
            alt={stock.name} 
            className="w-12 h-12 rounded-full bg-gray-100" 
          />
          <div>
            <h2 className="text-xl font-bold text-gray-900">{stock.name}</h2>
            <span className="text-sm text-gray-500">{stock.symbol}</span>
          </div>
        </div>
        
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="p-4 bg-gray-50 rounded-lg">
            <span className="text-sm text-gray-500">Price</span>
            <p className="text-2xl font-bold text-gray-900">${stock.price}</p>
          </div>
          <div className="p-4 bg-gray-50 rounded-lg">
            <span className="text-sm text-gray-500">Change</span>
            <p className={`text-2xl font-bold ${stock.change >= 0 ? 'text-green-600' : 'text-red-600'}`}>
              {stock.change >= 0 ? '+' : ''}{stock.change}%
            </p>
          </div>
          <div className="p-4 bg-gray-50 rounded-lg">
            <span className="text-sm text-gray-500">Volume</span>
            <p className="text-2xl font-bold text-gray-900">2.1M</p>
          </div>
        </div>
      </div>
    </div>
  );
}

// Main Dashboard Component
function Dashboard() {
  const [activeTab, setActiveTab] = React.useState('tfsa');

  function handleTabChange(tab) {
    setActiveTab(tab);
  }

  function getMainContent() {
    if (activeTab === 'tfsa') return <TFSAContent />;
    if (activeTab === 'resp') return <RESPContent />;
    return <StockContent stockId={activeTab} />;
  }

  return (
    <div className="flex min-h-screen bg-gray-50">
      {/* Sidebar */}
      <div className="fixed w-64 h-full bg-gray-900 text-white p-6">
        <div className="mb-8 pb-4 border-b border-gray-700">
          <h2 className="text-xl font-bold">Finance Dashboard</h2>
        </div>

        {/* Accounts Section */}
        <div className="mb-6">
          <h3 className="text-sm font-semibold text-gray-400 uppercase mb-3">
            Accounts
          </h3>
          <nav className="space-y-1">
            <SidebarButton 
              onClick={handleTabChange} 
              tabId="tfsa" 
              isActive={activeTab === 'tfsa'}
            >
              TFSA
            </SidebarButton>
            <SidebarButton 
              onClick={handleTabChange} 
              tabId="resp" 
              isActive={activeTab === 'resp'}
            >
              RESP
            </SidebarButton>
          </nav>
        </div>

        {/* Stocks Section */}
        <div>
          <h3 className="text-sm font-semibold text-gray-400 uppercase mb-3">
            Stocks
          </h3>
          <nav className="space-y-1">
            <SidebarButton 
              onClick={handleTabChange} 
              tabId="tesla" 
              isActive={activeTab === 'tesla'}
            >
              Tesla
            </SidebarButton>
            <SidebarButton 
              onClick={handleTabChange} 
              tabId="blackrock" 
              isActive={activeTab === 'blackrock'}
            >
              BlackRock
            </SidebarButton>
            <SidebarButton 
              onClick={handleTabChange} 
              tabId="pepsico" 
              isActive={activeTab === 'pepsico'}
            >
              PepsiCo
            </SidebarButton>
          </nav>
        </div>
      </div>

      {/* Main Content */}
      <div className="ml-64 flex-1">
        {getMainContent()}
      </div>
    </div>
  );
}

export default Dashboard;
